  return (
    <div className=' flex flex-col items-center'>
      {/* text */}
      <div >
        <div className=' text-[#000814] text-[36px] font-inter font-semibold leading-[44px] tracking-[-0.72px]'>Your swiss knife for <HighlightText text={"learning any language"}></HighlightText> </div>
        <div className='text-[16px] font-inter font-semibold leading-[44px] tracking-[-0.72px]'>Using spin making learning multiple languages easy. with 20+ languages realistic voice-over, progress tracking, custom schedule and more.</div>
      </div>

    </div>
  )
}

export default LearningLanguageSection
