import React from 'react'

const CourseCard = ({active,title,subcontent}) => {
  return (
    // active true means white card 
    <div className={`${active?" bg-white text-black":" bg-richblack-600 text-white"} h-[300px] w-[32%]
    flex flex-col justify-between`}>
      
      {/* title and content */}
      <div className='flex flex-col px-[24px] pt-[32px] pb-[52px]'>
        <div className='text-[20px] font-semibold font-inter text-richblack-800'>{title}</div>
        <div className='text-[16px] text-richblack-500 font-inter'>{subcontent}</div>
      </div>

      {/* footer of card */}
      <div className='flex justify-between border-t-[4px] border-dashed border-richblack-50 px-[24px] py-[16px]'>
        {/* beginner */}
        <div>
            {/* <img src={miniusers}></img> */}
            Beginner
        </div>
        {/* lessons */}
        <div>
            {/* <img src={charttree}></img> */}
            6 Lessons
        </div>
      </div>
    </div>
  )
}

export default CourseCard
